package server;import com.sun.net.httpserver.HttpServer;import manager.TaskManager;import server.handlers.*;import java.io.IOException;import java.net.InetSocketAddress;public class HttpTaskServer {    private static final int PORT = 8080;    private HttpServer httpServer;    private final TaskManager manager;    public HttpTaskServer(TaskManager manager) {        this.manager = manager;    }    public void start() throws IOException {        httpServer = HttpServer.create(new InetSocketAddress(PORT), 0);        httpServer.createContext("/tasks", new TaskHandler(manager));        httpServer.createContext("/subtasks", new SubTaskHandler(manager));        httpServer.createContext("/epics", new EpicHandler(manager));        httpServer.createContext("/history", new HistoryHandler(manager));        httpServer.createContext("/prioritized", new PrioritizedHandler(manager));        httpServer.start();    }    public void stop() {        httpServer.stop(0);    }}